interface MarketplaceItem {
  id: string;
  name: string;
  description: string;
  price: number;
  category: string;
  icon: string;
  image: string;
  availability: string;
  rating: number;
  features: string[];
}

const ItemImage = ({ category, itemId }: { category: string; itemId: string }) => {
  const getImageUrl = (id: string) => {
    switch (id) {
      case "certificate":
        return "https://plus.unsplash.com/premium_photo-1682075199505-e96c80e84e5a?w=600&auto=format&fit=crop&q=60&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxzZWFyY2h8MTd8fGNlcnRpZmljYXRlJTIwc2Nob29sfGVufDB8fDB8fHww";
      case "chapan-bhog":
        return "data:image/jpeg;base64,";
      case "prasada":
        return "data:image/jpeg;base64,";
      case "diya":
        return "https://media.istockphoto.com/id/2184643825/photo/full-frame-image-of-thali-containing-diwali-oil-lamps-unrecognisable-women-selecting-mitti.webp?a=1&b=1&s=612x612&w=0&k=20&c=Xc88v93RTqYZjyxEUGb88BiewiyxEpBiGSbiyt6QG64=";
      case "shegra-darshan":
        return "https://images.unsplash.com/photo-1582510003544-4d00b7f74220?w=400&h=300&fit=crop&crop=center";
      case "aarti-pass":
        return "https://images.unsplash.com/photo-1578662996442-48f60103fc96?w=400&h=300&fit=crop&crop=center";
      default:
        return "https://images.unsplash.com/photo-1582510003544-4d00b7f74220?w=400&h=300&fit=crop&crop=center";
    }
  };

  const getAltText = (id: string) => {
    switch (id) {
      case "certificate":
        return "Spiritual journey certificate with traditional design";
      case "chapan-bhog":
        return "Traditional Indian sweets and prasadam offerings";
      case "prasada":
        return "Sacred temple prasada and offerings";
      case "diya":
        return "Traditional brass diya lamps lit for worship";
      case "shegra-darshan":
        return "Temple entrance for fast darshan access";
      case "aarti-pass":
        return "Temple aarti ceremony with oil lamps";
      default:
        return "Spiritual marketplace item";
    }
  };

  return (
    <div className="relative h-48 overflow-hidden">
      <img
        src={getImageUrl(itemId)}
        alt={getAltText(itemId)}
        className="w-full h-full object-cover transition-transform duration-300 hover:scale-105"
      />
      {/* Overlay gradient for better text readability */}
      <div className="absolute inset-0 bg-gradient-to-t from-black/40 via-transparent to-transparent"></div>
      
      {/* Optional category badge overlay */}
      <div className="absolute bottom-2 left-2">
        <div className="bg-white/90 backdrop-blur-sm px-2 py-1 rounded-full text-xs font-medium text-gray-800">
          {category}
        </div>
      </div>
    </div>
  );
};

