export default function ClaimKarmaPage() {
  const [cart, setCart] = useState<string[]>([]);
  const [userPoints] = useState(450); // This would come from your user data

  const addToCart = (itemId: string) => {
    setCart([...cart, itemId]);
  };

  const removeFromCart = (itemId: string) => {
    setCart(cart.filter(id => id !== itemId));
  };

  const isInCart = (itemId: string) => cart.includes(itemId);

  const getTotalCost = () => {
    return cart.reduce((total, itemId) => {
      const item = marketplaceItems.find(i => i.id === itemId);
      return total + (item?.price || 0);
    }, 0);
  };

  const canAfford = (price: number) => userPoints >= price;

  const getCategoryIcon = (category: string) => {
    switch (category) {
      case "Certificate": return <Award className="h-4 w-4" />;
      case "Prasadam": return <Gift className="h-4 w-4" />;
      case "Spiritual Items": return <Flame className="h-4 w-4" />;
      case "Darshan": return <Eye className="h-4 w-4" />;
      case "Spiritual Experience": return <Star className="h-4 w-4" />;
      default: return <Gift className="h-4 w-4" />;
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold mb-4 bg-gradient-to-r from-orange-600 to-amber-600 bg-clip-text text-transparent">
            Spiritual Marketplace
          </h1>
          <p className="text-gray-600 text-lg">
            Exchange your Punya Points for sacred items and spiritual experiences
          </p>
          <div className="mt-4 flex items-center justify-center gap-4">
            <Badge variant="outline" className="text-lg px-4 py-2">
              Available Points: {userPoints} ‚≠ê
            </Badge>
            <Badge variant="outline" className="text-lg px-4 py-2">
              Cart: {cart.length} items
            </Badge>
          </div>
        </div>

        {/* Marketplace Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mb-8">
          {marketplaceItems.map((item) => (
            <Card key={item.id} className="overflow-hidden hover:shadow-lg transition-shadow">
              <div className="relative">
                <ItemImage category={item.category} itemId={item.id} />
                <Badge 
                  className="absolute top-2 right-2" 
                  variant={item.availability === "Limited" ? "destructive" : "secondary"}
                >
                  {item.availability}
                </Badge>
              </div>
              
              <CardHeader>
                <div className="flex items-center gap-2 mb-2">
                  {getCategoryIcon(item.category)}
                  <Badge variant="outline">{item.category}</Badge>
                </div>
                <CardTitle className="text-xl">{item.name}</CardTitle>
                <CardDescription>{item.description}</CardDescription>
                
                <div className="flex items-center gap-2">
                  <div className="flex items-center">
                    {[...Array(5)].map((_, i) => (
                      <Star 
                        key={i} 
                        className={`h-4 w-4 ${i < Math.floor(item.rating) ? 'fill-yellow-400 text-yellow-400' : 'text-gray-300'}`} 
                      />
                    ))}
                  </div>
                  <span className="text-sm text-gray-600">({item.rating})</span>
                </div>
              </CardHeader>

              <CardContent>
                <div className="space-y-3">
                  <div className="text-2xl font-bold text-orange-600">
                    {item.price} Points
                  </div>
                  
                  <div className="space-y-1">
                    {item.features.map((feature, index) => (
                      <div key={index} className="flex items-center gap-2 text-sm text-gray-600">
                        <div className="w-1 h-1 bg-orange-400 rounded-full"></div>
                        {feature}
                      </div>
                    ))}
                  </div>

                  <Button 
                    onClick={() => isInCart(item.id) ? removeFromCart(item.id) : addToCart(item.id)}
                    disabled={!canAfford(item.price)}
                    className={`w-full ${
                      isInCart(item.id) 
                        ? 'bg-green-600 hover:bg-green-700' 
                        : canAfford(item.price)
                        ? 'bg-orange-600 hover:bg-orange-700'
                        : 'bg-gray-400 cursor-not-allowed'
                    }`}
                  >
                    {isInCart(item.id) ? (
                      <>
                        <ShoppingCart className="h-4 w-4 mr-2" />
                        Added to Cart
                      </>
                    ) : canAfford(item.price) ? (
                      <>
                        <ShoppingCart className="h-4 w-4 mr-2" />
                        Add to Cart
                      </>
                    ) : (
                      <>
                        <Clock className="h-4 w-4 mr-2" />
                        Insufficient Points
                      </>
                    )}
                  </Button>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>

        {/* Cart Summary */}
        {cart.length > 0 && (
          <Card className="bg-gradient-to-r from-orange-50 to-amber-50 border-orange-200">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <ShoppingCart className="h-5 w-5" />
                Cart Summary
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                <div className="space-y-2">
                  {cart.map((itemId, index) => {
                    const item = marketplaceItems.find(i => i.id === itemId);
                    return (
                      <div key={index} className="flex justify-between items-center">
                        <span>{item?.name}</span>
                        <span className="font-semibold">{item?.price} Points</span>
                      </div>
                    );
                  })}
                </div>
                
                <div className="border-t pt-4">
                  <div className="flex justify-between items-center text-lg font-bold">
                    <span>Total Cost:</span>
                    <span className="text-orange-600">{getTotalCost()} Points</span>
                  </div>
                  <div className="flex justify-between items-center text-sm text-gray-600 mt-1">
                    <span>Remaining Balance:</span>
                    <span>{userPoints - getTotalCost()} Points</span>
                  </div>
                </div>

                <Link href="/claim-karma/checkout">
                  <Button 
                    className="w-full bg-green-600 hover:bg-green-700 text-white"
                    disabled={getTotalCost() > userPoints}
                  >
                    Proceed to Checkout ({getTotalCost()} Points)
                  </Button>
                </Link>
              </div>
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}